class Solution {
    public int[] shortestToChar(String s, char c) {
        int[] ans = new int[s.length()];
        int prev = - s.length(); // to assure the i - prev in first scan is bigger than any possible result;
        
        for(int i = 0; i < s.length(); i++){
            if(s.charAt(i) == c) prev = i;
            ans[i] = i - prev;   
        }
        
        for(int i = prev; i >= 0; i--){ // prev not pos
            if(s.charAt(i) == c) prev = i;
            ans[i] = Math.min(prev - i, ans[i]);   
        }
        
        return ans;
    }
}
