       let s = [];
        let flag = false;
        let nl = num.length;
        while(nl != 0 || k != 0) {
            let a = 0, b = k % 10 ;
            if(nl !== 0) a = num[--nl];
            k = parseInt(k/10);

            let ans = a + b;

