class Solution {
    // 思路： 1、将数组的最后一个元素和k的个位开始相加，
    //       2、大于10 就向前进一位（k的前一位 + 1）
     //        3、 重复以上步骤直到数组和k的位数结束
    public List<Integer> addToArrayForm(int[] num, int k) {
     List<Integer> rest = new LinkedList<>();
    int len = num.length - 1;
    for(int i = len;i >= 0;i--) {
        int total = num[i] + k % 10;
        //取 除过后一位的前面的值
        k /= 10;
        if(total >= 10) {
            //大于10，则前面的值需要进1
            k++;
            total = total % 10;
        }
        rest.add(total);
    }
    while(k > 0) {
        rest.add(k % 10);
        k /=10;
    }
     Collections.reverse(rest);
     return rest;
    }
}
