    for(let i = 0; i < s.length; i++) {
        if (s[i] === c) {
            res[i] = i
        } else [
            res[i] = res[i-1] == void 0 ? Infinity : res[i-1]
        ]
    }

    for(let i = s.length -1; i >= 0; i--) {
        if (res[i] == Infinity || i - res[i] > res[i+1] - i) {
            res[i] = res[i+1]
        }
    }

    for (let i = 0; i < res.length; i++) {
        res[i] = Math.abs(i -res[i])
    }
