```java
class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> ans = new LinkedList<>();
        int len = num.length;
        int i = len - 1, tmp = k, carry = 0;
        while (i >= 0 || tmp > 0 || carry > 0) {
            int t = (i >= 0 ? num[i] : 0) + (tmp > 0 ? tmp % 10 : 0) + carry;
            carry = t / 10;
            ans.add(0, t % 10);
            --i;
            tmp /= 10;
        }
        return ans;
    }
}
