class Solution:

    def addToArrayForm(self, num: List[int], k: int) -> List[int]:
        A = num[::-1]
        B = []
        # 将 k 变数组。
        while k:
            B.append(k % 10)
            k = (k - k % 10) // 10

        # A比 B 短的时候。
        if len(A) < len(B):
            A, B = B, A

        # 把 B加到 A 中。
        for i in range(len(B)):
            A[i] = A[i] + B[i]

        # A 中超过 10 的做进位，超过 A 长度的做列表添加。
        for i in range(len(A)):
            if A[i] >= 10:
                if i < len(A) - 1:
                    A[i + 1] = A[i + 1] + A[i] // 10
                    A[i] = A[i] % 10
                else:
                    A.append(A[i] // 10)
                    A[i] = A[i] % 10

        return A[::-1]
```

