class Solution {
    public int[] shortestToChar(String s, char c) {
        int[] ans = new int[s.length()];
        //Arrays.fill(ans, s.length() + 1);
        int i = 0, j = s.length() - 1, tmp = -1;
        while (i < s.length()) {
