        List<Integer> res = new ArrayList<Integer>();
        int n = A.length;
        for(int i = n - 1;i >= 0;i--){
            int sum = A[i] + K % 10;       
            K /= 10;    
            if(sum >= 10){
                K++;                       
                sum -= 10;                  
            }
            res.add(sum);
        }
        while (K > 0) {
            res.add(K % 10);
            K /= 10;
        }
        Collections.reverse(res);
        return res;
    }
}
