    vector<int> addToArrayForm(vector<int>& num, int k) {
        int carry  = 0;//进位
        int i = num.size() - 1;
        vector<int> vec;

        while(i >= 0 || k != 0)
        {
            int n1 = i >= 0 ? num[i] : 0;
            int n2 = k >= 0 ? k % 10 : 0;

