class Solution {
    public int[] shortestToChar(String s, char c) {
        int[] result = new int[s.length()];
        int cIndex = -1;
        for(int i=0; i<s.length(); i++){
            if(s.charAt(i) == c){
                result[i]=0;
                cIndex = i;
                continue;
            }

            if(cIndex == -1){
                result[i]=-1;
                continue;
            }else{
                result[i]=i-cIndex;
