       k = Math.floor(k / 10);
       if(sum >= 10) {
           k++;
           sum -= 10;
       } 
       res.push(sum);
   }
   for(; k > 0; k = Math.floor(k / 10)) {
       res.push(k % 10);
   }
   res.reverse();
