var addToArrayForm = function(num, k) {
    let kArr = k.toString().split('').map(item => parseInt(item));
    let maxArr, minArr, a = 0;
    if(kArr.length > num.length) {
        maxArr = kArr.reverse();
        minArr = num.reverse();
