class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> res = new LinkedList<>();
        int idx = num.length-1;
        int carry = 0;
        while (idx >= 0 || k > 0) {
            int x = idx >= 0 ? num[idx] : 0;
            int y = k > 0 ? k % 10 : 0;
            int sum = x+y+carry;
            res.add(0, sum%10);
            carry = sum / 10;
            idx--;
            k /= 10;
        }
        if (carry == 1) {
            res.add(0, 1);
        }
        return res;
    }
}
