var addToArrayForm = function(num, k) {
    let cur = String(k).split('');
    let res = [];
    let temp = []
    while(num.length > 0 || cur.length > 0 || temp.length > 0) {
        let a = Number(num.pop() || 0);
        let b = Number(cur.pop() || 0);
        let c = Number(temp.pop() || 0);
