        int flag = 0;
        for(int i = nums.size() - 1; i > -1; i--)
        {
            tmp = nums[i];
            if(k > 0)
            {
                tmp += k % 10;
                k = k / 10;
            }
            tmp += flag;
            data.push(tmp % 10);
            flag = tmp / 10;
        }
        while(k > 0)
        {
            tmp = flag + k % 10;
            k = k / 10;
            data.push(tmp % 10);
            flag = tmp / 10;
        }
        if(flag)
            data.push(flag);
        vector<int> res;
