class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        map<int,int> nummap;
        vector<int> revec;
        int n=num.size(),flag=0;
        reverse(num.begin(),num.end());
        for(int i=0;i<n;i++){
            nummap[i]=num[i];
        }
        int i=0;
        while(k!=0){
            nummap[i]+=k%10;
            k=k/10;
            i++;
