            if(it->second>=10&&it->first<nummap.size()-1){
                it->second-=10;
                it++;
                it->second+=1;
                it--;
            }
            if(it->second>=10&&it->first==nummap.size()-1){
                it->second-=10;
                flag++;
            }
            revec.push_back(it->second);
        }
        if(flag) revec.push_back(1);
        reverse(revec.begin(),revec.end());
        return revec;
    }
};

