class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int> res = {num.rbegin(), num.rend()};
        int len = 0;
        while (k!=0) {
            int temp = k % 10;
            k /= 10;
            if (len < res.size()) {
                if (temp + res[len] >= 10) {
                    k += 1;
                }
                res[len] = (res[len] + temp) % 10;
            }
            else {
                res.emplace_back(temp);
            }
            ++len;
        }
        return {res.rbegin(), res.rend()};
    }
};
