		int a= num.length;
		int b = kStrs.length;
		int max = a;
		if (b>a){
			max = b;
		}
		List <Integer> res = new ArrayList<>();
		int next = 0;
		for (int j = 1;j<=max;j++) {
			int num1 = 0;
			int num2 = 0;
			if (a - j >=0) {
				num1 = num[a-j];
			}
			if (b- j >= 0) {
				num2 = Integer.valueOf(kStrs[b-j]);
			}
			int cur = num1+num2+next;
			if (cur>=10) {
				res.add(cur%10);
				next = 1;
			} else {
				res.add(cur);
				next = 0;
			}
		}
		return Collections.reverse(res);
}

