class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        int t = k;
        int i = 0;
        vector<int> res;
        while (t) {
            if (i < num.size()) {
                t += num[num.size() - i - 1];
                i++;
            }
            res.push_back(t % 10);
            t /= 10;
        }
        while (i < num.size()) {
