class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int> res;
        int carry = 0, len = num.size() - 1;
        int tmparr = 0, tmpk = 0, sum = 0;
        int i = len;
        while (i >= 0 || k != 0) {
            tmparr = 0;
            tmpk = 0;
            if (k != 0) {
                tmpk = k % 10;
                k /= 10;
            }
            if (i >= 0) {
                tmparr = num[i];
                --i;
