            sum = tmparr + tmpk + carry;
            carry = sum / 10;
            res.emplace_back(sum % 10);    
        }

        if (carry) {
            res.emplace_back(carry);
        }

        reverse(res.begin(), res.end());

        return res;
    }
};

```


