    vector<int> addToArrayForm(vector<int>& num, int k) {
        int carry = 0;
        vector<int> res;
        for (int i = num.size() - 1; i >= 0; i--) {
            res.push_back((carry + num[i] + k % 10) % 10);
            carry = (carry + num[i] + k % 10) / 10;
            k /= 10;
        }
        carry = carry + k;
        while (carry) {
            res.push_back(carry % 10);
            carry /= 10;
        }
        reverse(res.begin(), res.end());
        return res;
    }
};
