class CustomStack {
    int[] arr = null;
    int top;
    public CustomStack(int maxSize) {
        arr = new int[maxSize];
        top = -1;
    }
    
    public void push(int x) {
        if(top < arr.length-1){
            arr[++top] = x;
        }
    }
    
    public int pop() {
        if(top >= 0){
            return arr[top--];
        } else {
            return -1;
        }
