class CustomStack {

    // declare an array to store the elements of the stack
    private int[] stack;
    // declare an integer to store the maximum size of the stack
    private int maxSize;
    // declare an integer to store the top index of the stack
    private int top;

    // constructor to initialize the stack with a given maxSize
    public CustomStack(int maxSize) {
        // initialize the array with the maxSize
        this.stack = new int[maxSize];
        // initialize the maxSize
        this.maxSize = maxSize;
        // initialize the top index to -1
        this.top = -1;
    }

    // method to push an element x to the top of the stack
    public void push(int x) {
        // check if the stack is full
        if (top == maxSize - 1) {
            // throw an exception
            throw new RuntimeException("Stack is full");
        } else {
            // increment the top index
            top++;
            // assign x to the top position of the array
            stack[top] = x;
        }
    }

    // method to pop an element from the top of the stack
    public int pop() {
        // check if the stack is empty
        if (top == -1) {
            // return -1 as per the requirement
            return -1;
        } else {
            // store the top element of the array in a variable
            int x = stack[top];
            // decrement the top index
            top--;
            // return x
            return x;
        }
    }

    // method to increment the bottom k elements of the stack by val
    public void inc(int k, int val) {
        // loop through the first k elements of the array or all elements if k > top + 1
        for (int i = 0; i < Math.min(k, top + 1); i++) {
            // increment each element by val
            stack[i] += val;
        }
    }
}
