class Solution {
    public int[] shortestToChar(String s, char c) {
        // 创建int型数组rt，用于存放最终的距离结果
        int[] rt = new int[s.length()];
        // 遍历字符串S
        for (int i = 0; i < s.length(); i++) {
            // 如果字符S中当前字符等于目标字符C，距离为0
            if (s.charAt(i) == c) {
                rt[i] = 0;
            }
            // 否则以当前字符为中心，从左右向外扩散查找，直到找到目标字符C
            else {
                int j = i - 1; // 左边字符的位置
                int k = i + 1; // 右边字符的位置
                while (j >= 0 || k < s.length()) {
                    // 如果左边字符的位置不越界且字符等于目标字符C，距离为当前字符和目标字符之间的距离
                    if (j >= 0 && s.charAt(j) == c) {
                        rt[i] = i - j;
                        break;
                    }
                    // 如果右边字符的位置不越界且字符等于目标字符C，距离为当前字符和目标字符之间的距离
                    else if (k < s.length() && s.charAt(k) == c) {
                        rt[i] = k - i;
                        break;
                    }
                    // 否则继续向外扩散查找
                    j--;
                    k++;
                }
            }
        }
        // 返回查找结果
        return rt;
    }
}
