```java
class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> ans = new ArrayList<>();
        int carry = k;
        for (int i = num.length - 1; i >= 0&&carry != 0; i--) {
            int sum = num[i] + carry;
            carry = sum / 10;
            num[i]=sum%10;
        }
        for(int i:num){
            ans.add(i);
        }
        while (carry > 0) {
            ans.add(0, carry % 10);
            carry = carry / 10;
        }
        return ans;
    }
}
