var shortestToChar = function(s, c) {
  const res = new Array(s.length).fill(Infinity)
  for (let i = 0; i < s.length; i++) {
    res[i] = s.substr(i, 1) === c ? 0 : (res[i - 1] != undefined ? res[i - 1] : Infinity) + 1
  }

