class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        vector<int> ans(s.size(), INT_MAX);
        int l = s.size(), r = -1, n = s.size();
        for (int i = 0; i < s.size(); i++) {
            int j = n - 1 - i;
            if (s[i] == c) l = i;
            if (s[j] == c) r = j;
            if (i >= l) ans[i] = min(ans[i], i - l);
            if (j <= r) ans[j] = min(ans[j], r - j);
        }
        return ans;
    }
};
