/**
    TC: O(max(N,logk)), N为数组的长度
    SC: O(max(N,logk))
*/

// Method 2: 将 加数 k 整个 加入数组表示的数的最低位
//           3 + 912 = 915, 5 留在当前这一位，将 910 / 10 = 91 以进位的形式加入下一位
class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> res = new LinkedList<>();
        int N = num.length;
        for (int i = N - 1; i >= 0; i--){
            int sum = num[i] + k;
            res.add(0, sum % 10);
            k = sum / 10;   // 更新 k 存进位 carry
        }
        while (k > 0) {
            res.add(0, k % 10);
            k /= 10;
        }
        return res;
    }
}

// Method 1: 两个数拆解后 同时 从后往前逐位相加
class Solution1 {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> res = new LinkedList<>();
        int N = num.length;

        int i = N - 1;
        int sum = 0, carry = 0;
        while (i >= 0 || k > 0) {
            int x = i >= 0 ? num[i] : 0;
            int y = k > 0 ? k % 10 : 0;
            sum = x + y + carry;
            carry = sum / 10;
            k /= 10;
            i--;
            res.add(0, sum % 10);
        }

        if (carry != 0) res.add(0, carry);
        return res;
    }
}
