class CustomStack {
public:
    vector<int> nums;
    int top = -1;
    CustomStack(int maxSize) {
        nums.resize(maxSize);
    }

    void push(int x) {
        if (top == nums.size()-1) {
            return;
        }
        nums[++top] = x;
    }

    int pop() {
        if (top == -1) {
            return -1;
        }
        return nums[top--];
    }

