class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        LinkedList<Integer> sum = new LinkedList<>();
        int i = num.length - 1;
        int carry = 0;
        while (i >= 0 || k > 0) {
            if (i >= 0) {
                carry += num[i--];
            }
            if (k > 0) {
                carry += k%10;
                k /= 10;
            }
            sum.addFirst(carry % 10 );
            carry /= 10;
        }
        if (carry > 0) {
            sum.addFirst(carry);
        }
