    class Solution {
        public int[] shortestToChar(String s, char c) {
            int[] res = new int[s.length()];
            int ch = c;

            for (int i = 0; i < s.length(); i++) {
                int a = s.indexOf(ch, i);
                int b = s.lastIndexOf(ch, i);
                if (a == i) {
                    res[i] = 0;
