```c++
class Solution {
public:
//每个字符的结果来自离它右侧和左侧最近的字符c中的较小值
//扫描两遍，第一遍更新所有c字符右侧字符的距离，第二遍更新所有c字符的左侧字符距离
    vector<int> shortestToChar(string s, char c) {
        int l = s.length();
        int dis = 0, i=0, j=l-1;
        vector<int>res(l,l);
        while(s[i]!=c && i<l)  i+=1;
        while(i <= l-1){
            if(s[i]==c) dis=i;
            res[i] = min(res[i],i-dis);

            i+=1;
        }

        while(s[j]!=c && j>=0)   j-=1;
        while(j>=0){
            if(s[j]==c) dis=j;
            res[j] = min(res[j],dis - j);

            j-=1;
        }   

        return res;  

    }
};
