### 代码
/**
 * @param {string} s
 * @param {character} c
 * @return {number[]}
 */
var shortestToChar = function (s, c) {
    const sArr = s.split('');
    const cResult = [];

    sArr.forEach((sItem, index) => {
        if (sItem === c) {
            cResult.push(index);
        }
    })
    const result = sArr.map((v, index) => {
        let min = s.length;
        cResult.forEach(value => {
            const nowMin = Math.abs(value - index)
            min = min < nowMin ? min : nowMin
        })
        return min
    })
    return result
};

