class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        vector<int> dis(s.size());
        int c_idx_1=0;
        int c_idx_2=1;

        for(int i = 0;i<s.size(); i++){
            if(s[i]==c){
                c_idx_2 = i;
                dis[i]=0;
                for(int j=c_idx_1; j<i;j++){
                    dis[j]=abs(c_idx_2-j);
                }
                c_idx_1 =c_idx_2+1;
            }else{
                dis[i]=abs(c_idx_2-i);
