#forward and backward array traverse
# tc: o(n) since we traverse the array without nest
# sc: o(n) for initialize ans array

class Solution:
    def shortestToChar(self, s: str, c: str) -> List[int]:
        ans = [float('inf')] * len(s)
        # traverse forward and backward to get two list, first the min value between those two list
        # correct way to initialize ans
        ans = [0 if s[i] == c else None for i in range(len(s))] # 0 means distance
        # forward scan
        for i in range(1, len(s)):
            if ans[i] != 0 and ans[i-1] is not None:
                ans[i] = ans[i-1] + 1
                # if i index is not c, and previous distance has value

        # backward scan
        for i in range(len(s)-2, -1, -1):
            if ans[i] is None or ans[i] > ans[i+1] + 1:
                ans[i] = ans[i+1] + 1 # not updated yet or value larger than backward scan
        return ans

