class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> ans = new ArrayList<>();
        int c = 0;

        int i = num.length-1 ; 

        int carry = 0;
        while(i>=0 || k >0){
          int ki = k % 10;
          int numi = i >= 0 ? num[i] : 0;

          k /= 10;
          i--;

          int sum = (ki + numi + carry) ;
