/**
 * @param {string} s
 * @param {character} c
 * @return {number[]}
 */

var shortestToChar = function(s, c) {
  let i=-1,j=0,cur=0;
  let res = []
  while(i<s.length){
    if(s[j]!==c && j<s.length){
      j++
      continue
    }

    if(i===-1){
      cur = 0
      while(cur<j){
        res.push(j-cur++)
      }
    }else if(j===s.length){
      cur = i
      while(cur<j){
        res.push(cur++-i)
      }
    }else{
      cur = i
      while(cur<j){
        res.push(Math.min(cur-i,j-cur))
        cur++
      }
    }
    i=j
    j++
  }
  return res
};

