class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int> res;
        int n = num.size();
        for(int i = n - 1; i >= 0; --i){
            int curSum = num[i] + k % 10;
            k /= 10;
            if(curSum >= 10){
                k++;
                curSum -=10;
            }
            res.push_back(curSum);
        }
        while(k > 0){
            res.push_back(k % 10);
            k /= 10;
        }
        reverse(res.begin(), res.end());
        return res;
    }
};
```

