class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        //把K直接加到数组num上
        int len = num.size() - 1;
        while (k > 0) {
            num[len] += k;
            k = num[len] / 10;
            num[len] %= 10;
            len --;

            if (len < 0 && k > 0) {
                num.insert(num.begin(), 0);
                len = 0;
            }
        }
        return num;
    }
};
