public List<Integer> addToArrayForm(int[] num, int k) {
        int carry = 0;
        List<Integer> res = new ArrayList<>();
        int i = num.length - 1;
        while (i >= 0 || k > 0) {
            int cur = (i >= 0 ? num[i] : 0) + (k > 0 ? k % 10 : 0) + carry;
            k = k > 0 ? k / 10 : 0;
            i = i >= 0 ? i - 1 : -1;
            carry = cur / 10;
            cur = cur % 10;
            res.add(cur);
        }
