int* addToArrayForm(int* num, int numSize, int k, int* returnSize){
    int resLen = fmax(numSize + 1, 5); // k 的取值范围 [1, 10^4]，加上进位最多是 5 位数
    int* res = malloc(sizeof(int) * resLen);
    int r, w;

    w = resLen - 1;
    r = numSize - 1;

    while (k || r >= 0) {
        if (r >= 0) k += num[r--];
        res[w--] = k % 10;
        k /= 10;
    }

    int realStart = w + 1;
    *returnSize = resLen - realStart;
    return res + realStart;
}
